using LargeFileSort.Entities;
using LargeFileSort.Parsers;
using LargeFileSort.Parsers.Abstract;

namespace Tests;

public class KeyValueLineParserTests
{
    private IFileLineParser<KeyValueFileLine> _parser;
        
    [SetUp]
    public void Setup()
    {
        _parser = new KeyValueFileLineParser();
    }
    
    [Test]
    public void StringOfLength2048IsParsed()
    {
        // Arrange
        const string line = "12312312312. ";
        const string expectedValue = "";
        
        // Act
        var result = _parser.Parse(line);

        // Assert
        Assert.That(result.Value.Length, Is.EqualTo(2048));
        Assert.That(result.Value.Length, Is.EqualTo(expectedValue.Length));
        Assert.That(result.Value, Is.EqualTo(expectedValue));
    }

    [Test]
    public void StringOfLength2049ReturnsNull()
    {
        // Arrange
        const string line = "12312312312. ";
        
        // Act
        var result = _parser.Parse(line);

        // Assert
        Assert.That(result, Is.Null);
    }

    [Test]
    public void NumberOfSizeLongMaxValueIsParsed()
    {
        // Arrange
        var line = $"{long.MaxValue}. TEST";
        
        // Act
        var result = _parser.Parse(line);

        // Assert
        Assert.That(result.Id, Is.EqualTo(long.MaxValue));
    }

    [Test]
    public void InvalidInputFormatThrowsAnException()
    {
        // Arrange
        const string line = "12312312312.";

        // Assert
        Assert.Throws<ArgumentOutOfRangeException>(() => _parser.Parse(line));
    }
}